    ITRC0->OUT_SEL[3][0] = 0xAAAAAAAAU;
    ITRC0->OUT_SEL[3][1] = 0xAAAAAAAAU;
    ITRC0->OUT_SEL_1[3][0] = 0xAAAAAAAAU;
    ITRC0->OUT_SEL_1[3][1] = 0xAAAAAAAAU;
    ITRC0->OUT_SEL_2[3][0] = 0xA0002AAAU;
    ITRC0->OUT_SEL_2[3][1] = 0xA0002AAAU;
    ITRC0->OUT_SEL[4][0] = 0xAAAAAAAAU;
    ITRC0->OUT_SEL[4][1] = 0xAAAAAAAAU;
    ITRC0->OUT_SEL_1[4][0] = 0xAAAAAAAAU;
    ITRC0->OUT_SEL_1[4][1] = 0xAAAAAAAAU;
    ITRC0->OUT_SEL_2[4][0] = 0xA0002AAAU;
    ITRC0->OUT_SEL_2[4][1] = 0xA0002AAAU;
    ITRC0->OUT_SEL[5][0] = 0xAAAAAAAAU;
    ITRC0->OUT_SEL[5][1] = 0xAAAAAAAAU;
