    /* Global Options */
    GPIO0->LOCK = 0;
    GPIO1->LOCK = 0;
    GPIO2->LOCK = 0;
    GPIO3->LOCK = 0;
    GPIO4->LOCK = 0;
    GPIO5->LOCK = 0;
